// Bootstrap 5 Minimal Implementation
(function() {
  'use strict';

  // Handle navbar collapse toggle
  const setupCollapseToggle = () => {
    const toggleButtons = document.querySelectorAll('.navbar-toggler');
    
    toggleButtons.forEach(button => {
      button.addEventListener('click', function(e) {
        e.preventDefault();
        const targetSelector = this.getAttribute('data-bs-target');
        if (!targetSelector) return;
        
        const target = document.querySelector(targetSelector);
        if (!target) return;
        
        // Toggle the show class
        target.classList.toggle('show');
        
        // Update aria-expanded
        const isExpanded = target.classList.contains('show');
        this.setAttribute('aria-expanded', isExpanded);
      });
    });

    // Close menu when clicking on a nav link
    const navLinks = document.querySelectorAll('.navbar-collapse a');
    navLinks.forEach(link => {
      link.addEventListener('click', function() {
        const collapseElement = this.closest('.navbar-collapse');
        if (collapseElement && collapseElement.classList.contains('show')) {
          collapseElement.classList.remove('show');
          const toggler = collapseElement.closest('.navbar').querySelector('.navbar-toggler');
          if (toggler) {
            toggler.setAttribute('aria-expanded', 'false');
          }
        }
      });
    });
  };

  // Initialize when DOM is ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', setupCollapseToggle);
  } else {
    setupCollapseToggle();
  }
})();
